import { Link } from "react-router-dom"
import React from "react";


function Inscription(){

    const baseURL = "http://localhost:5000/reservation";
    const patientValues = {
        firstname: "",
        lastname: "",
        ramq:"",
        email:"",
        password:""
    }

    const [patient, setPatient] = React.useState(patientValues);
    const[formErrors, setFormErrors] = React.useState({});
    function handleChange(event) {
        setPatient(
            {
            ...patient,
            [event.target.name] : [event.target.value]
            }
    )}
    function isValidE(email) {
        let regex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        let flag = regex.test(email)
        return flag;

    };
    function isValidR(ramq) {
        let regex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        let flag = regex.test(email)
        return flag;

    };
    function DataValidation(inputValues) {
        const errors ={};
        if(!inputValues.lastname || !inputValues.firstname
            || !inputValues.email || !inputValues.ramq || !inputValues.password){
            errors.value = "Aucun champ ne doit etre vide ! "
        }
        if(!inputValues.p){
            errors.prenom = "Ce champ de peut etre vide"
        }
        if(!inputValues.telephone){
            errors.telephone = "Ce champ de peut etre vide"
        }
        return errors;
    }
    function handleClick(event){
        event.preventDefault();
        setFormErrors(DataValidation(formValues))
    }



    return(


        <form className="hero">
            <div className="col-12">
                <h1>Inscriptions</h1>
                <form  method="post">
                <div>
            Prénom: <br />
            <input onChange={handleChange} type="text" name="firstname" value={patientValues.firstname}/>
          </div>
          
          <div>
            Nom: <br />
            <input onChange={handleChange} type="text" name="lastname" value={patientValues.lastname}/>
          </div>

          <div>
            RAMQ: <br />
            <input onChange={handleChange} type="text" name="ramq" maxLength={12} value={patientValues.ramq}/>
          </div>

          <div>
            Email: <br />
            <input onChange={handleChange} type="text" name="email" value={patientValues.email}/>
          </div>

          <div>
            Password: <br />
            <input onChange={handleChange} type="password" name="password" value={patientValues.password}/>
          </div>
          <br></br>
          <input type="checkbox" name="TermsOfUse" value="" /> &nbsp;    
          
              <Link to="/TermsOfUse">Accept terms of use.</Link>
         
            <br></br><br></br>
          <button onClick={handleClick} type="button" className="btn btn-primary">Inscrivez-vous</button>
            </form>
            </div>

        </form>
    )


}
export default Inscription;
