import axios from "axios";
import React, {useEffect} from "react";

function RechercherDocteur(){


    const getDocteur = "http://localhost:5000/docteur";
    const [docteur, setDocteur] = React.useState([]);
    const [rechercherDocteur, SetRechercherDocteur] = React.useState([]);

useEffect(() => {
    axios.get(getDocteur)
    .then(res => setDocteur(res.data) )
    .catch(err => console.log(err))
},[])

const handleRechercherDocteur = (e) => {
    let value = e.target.value;
    SetRechercherDocteur(value)
    value.length > 2 
}

    
    return(
        <div className="hero">
            <div className="col-12">
            <div className="rechercheBar">
                <input
                type="text"
                name="recherche"
                id="recherche"
                placeholder=""
                onChange={handleRechercherDocteur}
                />
            </div>

            <div className="resultat"></div>
            <h1>Résultat à votre recherche  </h1>
                <table>
                    <thead>
                    <tr>
                        <th>Nom</th>

                        <th>Prenom</th>

                        <th>Spécialité</th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        docteur.filter((user) => {
                            <td>{user.firstname.includes(rechercherDocteur)}</td>
                        })
                        .map((user,i) => (
                            <tr>
                                <td>{user.firstname}</td>
                                <td>{user.lastname}</td>
                            </tr>
                        ))
                    }
                    </tbody>
                </table>
        </div>
    </div>
    )
}

export default RechercherDocteur;
