import { Link } from "react-router-dom"
import React from "react";


function Inscription(){

    const baseURL = "http://localhost:5000/reservation";
    const patientValues = {
        firstname: "",
        lastname: "",
        ramq:"",
        email:""
    }

    const [patient, setPatient] = React.useState(patientValues);
    const[formErrors, setFormErrors] = React.useState({});
    function handleChange(event) {
        setPatient(
            {
            ...patient,
            [event.target.name] : [event.target.value]
            }
    )}
    function DataValidation(inputValues) {
        const errors ={};
        if(!inputValues.nom){
            errors.nom = "Ce champ de peut etre vide"
        }
        if(!inputValues.prenom){
            errors.prenom = "Ce champ de peut etre vide"
        }
        if(!inputValues.telephone){
            errors.telephone = "Ce champ de peut etre vide"
        }
        return errors;
    }
    function handleClick(event){
        event.preventDefault();
        setFormErrors(DataValidation(formValues))
    }



    return(


        <form className="hero">
            <div className="col-12">
                <h1>Inscriptions</h1>
                <form  method="post">
                <div>
            Prénom: <br />
            <input onChange={handleChange} type="text" name="firstname" value="firstname"/>
          </div>
          
          <div>
            Nom: <br />
            <input onChange={handleChange} type="text" name="lastname" value="lastname"/>
          </div>

          <div>
            RAMQ: <br />
            <input onChange={handleChange} type="text" name="ramq" maxLength={12} value="ramq"/>
          </div>

          <div>
            Email: <br />
            <input onChange={handleChange} type="text" name="email" value="email"/>
          </div>

          <div>
            Password: <br />
            <input onChange={handleChange} type="password" name="password" value="password" />
          </div>
          <br></br>
          <input type="checkbox" name="TermsOfUse" value="" /> &nbsp;    
          
              <Link to="/TermsOfUse">Accept terms of use.</Link>
         
            <br></br><br></br>
          <button onClick={handleClick} type="button" className="btn btn-primary">Inscrivez-vous</button>
            </form>
            </div>

        </form>
    )


}
export default Inscription;
