import React from "react";
import { useState } from "react";

function Taches({texte,supprimer}){


    const [estActif, changer] = useState(true);


    const handleButton2 = ()=>{
        changer(!estActif)
    }


    return(
        <div className="d-flex justify-content-end">
            <div className="col-6">
                {estActif? texte:<del>{texte}</del>}
            </div>
            <div className="col-4">
                <button onClick={handleButton2} className={estActif ? "btn btn-secondary":"btn btn-success"}>
                    {estActif ? "A faire":"Terminer"}
                </button>
            </div >
            <div className="col-2 ">
                <div>
                    <button onClick={supprimer} className="btn btn-danger">Supprimer</button>
                </div>
            </div>
        </div>
    )

}



function TODO (){
    const [listItems, setListe] =useState([])
    const[unItem,setItem]=useState("")

    const handleButton=()=>{
        setListe([unItem,...listItems])
    }
    const handleText=(ev) =>{
        setItem(ev.target.value)
    }

    const supprimer=(index)=>{
        const newList=[...listItems]
        newList.splice(index,1)
        setListe(newList)
    }


    return (
        <div >
            <h1>Liste des taches</h1>
            <input onChange={handleText} type="text" />
            <button onClick={handleButton}>Ajouter</button>

            <table className="table table-striped">
                <tbody>
                {
                    listItems.map((e)=>
                        <tr>
                            <td>
                                <Taches texte={e} supprimer={supprimer} />
                            </td>
                        </tr>
                    )

                }
                </tbody>
            </table>

        </div>
    )

}

export default TODO;
